// Agent 1: Job Scraper
const agent1 = {
  name: "Agent 1 - Job Scraper",
  role: "Data Collection",
  timestamp: new Date().toISOString()
};

console.log(`ðŸ¤– ${agent1.name}: Starting job scraping process`);

const jobs = [];
for (const item of $input.all()) {
  const response = item.json;

  if (response.data && Array.isArray(response.data)) {
    const jobsToProcess = response.data.slice(0, 8); // Limit for demo

    jobsToProcess.forEach((job, index) => {
      if (job.title && job.company_name) {
        jobs.push({
          json: {
            // Core job data
            id: job.slug || `job_${Date.now()}_${index}`,
            title: job.title,
            company: job.company_name,
            location: job.location || 'Remote',
            description: job.description ? 
                         job.description.replace(/<[^>]*>/g, '').substring(0, 400) : 
                         'No description available',
            url: job.url || `https://arbeitnow.com/jobs/${job.slug}`,
            source: 'Arbeitnow',

            // Multi-agent tracking
            processed_by: [agent1.name],
            agent_chain: "Agent1",
            batch_id: `batch_${Date.now()}`,
            job_index: index,
            scraped_at: agent1.timestamp,
            current_agent: agent1.name
          }
        });
      }
    });
  }
}

console.log(`âœ… ${agent1.name}: Successfully scraped ${jobs.length} jobs â†’ Passing to Agent 2`);
return jobs;

